import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001', // replace with your backend URL
});

const catchPokemon = async (name, nickname) => {
  const response = await api.post('/pokemon/catch', { name, nickname });
  return response.data;
};


const releasePokemon = async () => {
  const response = await api.post('/pokemon/release');
  return response.data;
};

const renamePokemon = async (nickname) => {
  const response = await api.post('/pokemon/rename', { nickname });
  return response.data;
};

const fetchPokemonList = async () => {
  const response = await api.get('/pokemon');
  return response.data;
};

export default {
  catchPokemon,
  releasePokemon,
  renamePokemon,
  fetchPokemonList,
};
