    } catch (error) {

    }
    setShowForm(true);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await catchPokemon(name, nickname);
      if (response.success) {
        const newPokemon = { id: Date.now(), name, nickname, image: pokemonDetail.image };
        dispatch({ type: 'CATCH_POKEMON', payload: newPokemon });
        setNickname('');
        setShowForm(false);
      } else {
        alert('Failed to catch Pokemon');
      }
    } catch (error) {
      console.error('Failed to catch Pokemon', error);
      alert('An error occurred while trying to catch the Pokemon');
    }
  };

  return (
    <div>
      <PokemonDetail pokemonDetail={pokemonDetail} onCatch={handleCatch} />
      {showForm && (
        <form onSubmit={handleSubmit}>
          <label>
            Nickname:
            <input
              type="text"
              value={nickname}
              onChange={(e) => setNickname(e.target.value)}
              required
            />
          </label>
          <button type="submit">Submit</button>
        </form>
      )}
    </div>
  );
};

export default PokemonDetailPage;
